cho $disabled_read;?> <?php echo $checked_read_submitted;?>>					<input type="checkbox" class="parent_<?php echo $tab ?>" name="<?php echo  $tab . '-' . $name; ?>[read][0]" value="1" <?php echo $disabled_read;?> <?php echo $checked_read_submitted;?>/>					<?php echo Yii::t('translations', 'Read Submitted');?>				</div>				<div class="chk read" style="width:160px" onclick="CheckOrUncheckInput(this)">					<div style="margin-left:10px" class="input<?php echo ($disabled_read) ? ' checkboxDisabled' : '';?>"></div>					<input type="hidden" value="0"  name="<?php echo  $tab . '-' . $name; ?>[read][1]" <?php echo $disabled_read;?> <?php echo $checked_read_approved;?>>					<input type="checkbox" class="parent_<?php echo $tab ?>" name="<?php echo  $tab . '-' . $name; ?>[read][1]" value="1" <?php echo $disabled_read;?> <?php echo $checked_read_approved;?>/>					<?php echo Yii::t('translations', 'Read Approved');?>				</div>				<div class="chk read" style="width:160px" onclick="CheckOrUncheckInput(this)">					<div style="margin-left:10px" class="input<?php echo ($disabled_read) ? ' checkboxDisabled' : '';?>"></div>					<input type="hidden" value="0"  name="<?php echo  $tab . '-' . $name; ?>[read][2]" <?php echo $disabled_read;?> <?php echo $checked_read_paid;?>>					<input type="checkbox" class="parent_<?php echo $tab ?>" name="<?php echo  $tab . '-' . $name; ?>[read][2]" value="1" <?php echo $disabled_read;?> <?php echo $checked_read_paid;?>/>					<?php echo Yii::t('translations', 'Read Paid');?>				</div>								<div class="chk write" style="width:160px" onclick="CheckOrUncheckInput(this)">					<div style="margin-left:10px" class="input<?php echo ($disabled_write) ? ' checkboxDisabled' : '';?>"></div>					<input type="checkbox" class="parent_<?php echo $tab ?>" name="<?php echo $tab . '-' . $name; ?>[write]" value="2"  <?php echo $disabled_write;?> <?php echo $checked_write;?>/>					<?php echo Yii::t('translations', 'Write');?>				</div>			</div>			<?php